const regexEmail = /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{3}$/i;

export const validation = (userData) => {

    const errors = {};

    if(!userData.email) errors.email = '*Campo obligatorio';
    else if(userData.email.length > 35) errors.email = '*No se puede superar los 35 caracteres';
    else if(!regexEmail.test(userData.email)) errors.email = '*Email no válido';

    if(!userData.password) errors.password = '*Campo obligatorio';
    else if (userData.password.length < 6 || userData.password.length > 10) errors.password = '*La password debe tener entre 6 y 10 caracteres';
    else if (!/\d/.test(userData.password)) errors.password = '*Debe contener al menos un número';
    
    return errors;
}

export const validationB = (userData) => {

    const errors = {};

    if(!userData.email) errors.email = '*Campo obligatorio';
    else if(userData.email.length > 35) errors.email = '*No se puede superar los 35 caracteres';
    else if(!regexEmail.test(userData.email)) errors.email = '*Email no válido';

    if(!userData.password) errors.password = '*Campo obligatorio';
    else if (userData.password.length < 6 || userData.password.length > 10) errors.password = '*La password debe tener entre 6 y 10 caracteres';
    else if (!/\d/.test(userData.password)) errors.password = '*Debe contener al menos un número';
    
    return errors;
}

