import { ADD_FAV, FILTER, ORDER, REMOVE_FAV } from './actions';

const initialState = {
    allCharacters: [],
    myFavorites: []
}

const rootReducer = (state = initialState, action) => {
    switch (action.type) {
        case ADD_FAV:
            return {
                ...state,
                myFavorites: [...state.allCharacters, action.payload],
                allCharacters: [...state.allCharacters, action.payload]
            };
        case REMOVE_FAV:
            return {
                ...state,
                myFavorites: state.myFavorites.filter(
                    (char) => char.id !== Number(action.payload)
                )
            };
        case FILTER:
            const filterCharacters = [...state.allCharacters];
            const aux = filterCharacters.filter(char =>
                char.gender === action.payload
            );
            return {
                ...state, myFavorites: aux
            }
            // return {
            //     ...state,
            //     myFavorites: state.allCharacters.filter(
            //        char => char.gender === action.payload 
            //     )
            // }
        // case ORDER:
        //     const orderCharacters = [...state.allCharacters].id.sort();
        //     if(action.payload === 'B') orderCharacters.reverse();
        //     return {
        //         ...state, myFavorites: orderCharacters
        //     }
        default:
            return { ...state };
    }
};

export default rootReducer;
