import { ADD_FAV, FILTER, REMOVE_FAV } from './actions';

const initialState = {
    myFavorites: [],
    allCharacters: []
}

const rootReducer = (state = initialState, action) => {
    switch (action.type) {
        case ADD_FAV:
            return { ...state, allCharacters: [...state.allCharacters, action.payload] };
        case REMOVE_FAV:
