const regexEmail = /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i;

const validation = (userData, errors, setErrors) => {

    // let validar = 0;
    // let arr = userData.password.split('')
    // arr.forEach(char => {
    //     if (!isNaN(Number(char))) validar++;
    // });

    if (!userData.email) setErrors({ ...errors, email: 'Debes llenar este campo' });
    if (userData.email.length > 35) setErrors({ ...errors, email: 'No puede superar los 35 caracteres' })
    if (!regexEmail.test(userData.email)) setErrors({ ...errors, email: 'Email incorrecto' });
    
    else setErrors({ ...errors, email: '' });

    if (userData.password.length < 6 || userData.password.length > 10) setErrors({ ...errors, password: 'Password incorrecta' });
    // // else if (validar < 1) setErrors({ ...errors, password: 'Debe tener al menos un número' });
    // else if (!/\d/.test(userData.password)) setErrors({ ...errors, password: 'Debe contener al menos un número' });
    // else setErrors({ ...errors, password: '' });
}

export default validation;
