import React from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";

export default function Deatil () {
    const [character, setCharacter] = useState({});
    const { id } = useParams();     // useParams es un objeto que se crea al pasar parámetros en el path de /detail con los dos puntos ":", se hace destructuring ya que le dimos el nombre "id" al parámetro

    useEffect(() => {
        axios(`https://rickandmortyapi.com/api/character/${id}`).then(({ data }) => {
           if (data.name) {
              setCharacter(data);
           } else {
              window.alert('No hay personajes con ese ID');
           }
        });
        return setCharacter({});
     }, [id]);

    return (
        <div>
            <h1>{character.name}</h1>
            {character.status && <h3>Status | {character.status}</h3>}
            {character.gender && <h3>Gender | {character.gender}</h3>}
            {character.species && <h3>Specie | {character.species}</h3>}
            <h3>{character?.origin?.name}</h3>
            {character.image && <img src={character.image} alt='300px'/>}
        </div>
    );
}
