import React, { useState } from "react";
import validation from "../../validation";
import './Form.css';

export default function Form({ login }) {
    const [userData, setUserData] = useState({
        email: '',
        password: ''
    });
    const [errors, setErrors] = useState({
        email: '',
        password: ''
    });
    const [valButton, setValButton] = useState(false);

    const handleChange = (event) => {
        const property = event.target.name;
        const value = event.target.value;
        setUserData({ ...userData, [property]: value });
        setErrors(
            validation({ ...userData, [property]: value })
        );
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        login(userData);
    };

    const disabledButton = () => {
        if(errors.email === '' && errors.password === '')
        return setValButton(true);
        else setValButton(false);
    }

    return (
        <div>
            <h2>¡Bienvenido!</h2>
            <form>
                <label htmlFor="email">Email</label> <br />
                <input
                    name='email'
                    placeholder="Email"
                    value={userData.email}
                    onChange={handleChange} />
                <br />
                <span className='warning'> {errors.email}</span>
                <br />

                <label htmlFor="password">Password</label> <br />
                <input
                    name='password'
                    type='password'
                    placeholder="Password"
                    value={userData.password}
                    onChange={handleChange} />
                <br />
                <span className='warning'> {errors.password}</span>
                <br />

                <button disabled={valButton} onClick={handleSubmit}>Login</button>
            </form>
        </div>
    );
}
